	(76, 189, '1', 'ENERO', 'ES UNA ALUMNA CON FACILIDAD PARA TRABAJAR EN EQUPO. CUMPLE CON LAS ENTREGAS SOLICITADAS Y TIENE DISPOSICIÓN PARA REALIZAR LAS ACTIVIDADES ASIGNADAS.', 'TRABAJAR EN REDACCIÓN TÉCNICA O FORMAL Y EN INICIATIVA PARA GENERAR PROPUESTAS.', '2022-02-05 21:18:13', 153, 'NO', NULL, NULL, NULL, '1392230652'),
	(77, 156, '1', 'ENERO', 'MEJORAR ESTRATEGIAS EMPRESARIALES ', 'MEJORAR ACTITUDES EMPRESARIALES ', '2022-02-08 12:06:00', 194, 'NO', NULL, NULL, NULL, '14531712'),
	(78, 157, '1', 'ENERO', 'MAS COMPROMISO CON EL DESARROLLO DE SU PLAN ', 'COMPROMISO E INVESTIGACIÓN ', '2022-02-08 12:20:36', 194, 'NO', NULL, NULL, NULL, '326704759'),
